import React, { useState } from 'react';
import "../student details/student profile.css"
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import white from '../images/white.jpg'
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


 const StudentProfile=()=>{
  const navigate = useNavigate();

  const [data, setData] = useState({
    name: '',
    mobile: '',
    age: '',
    qualification: ''
  })

 const handleChange = (event) => {
  const filterFormDataCopy = { ...data };
  filterFormDataCopy[event.target.name] = event.target.value;
  setData(filterFormDataCopy);
 };

 const handleSubmit = async(event) => {
  event.preventDefault();
  console.log(data, 'data')
  const request = {
    method: "POST",
    data: data,
    url: "http://localhost:8000/api/add",
  };
  await axios(request).then((res) => {
    console.log(res, 'res-');
    alert("succesfullt applied")
    navigate('/details')
  })
   }


  return(
    <div className='main'>
      <h3 className='heading'>School Admission Form</h3>
       <img src={white} alt="white" />
     
      <form className='student' onClick={handleSubmit}>
        
        <div>
          <input type="text" name="name" placeholder='User Name'  onChange={handleChange}/>
        </div>
        <div>
          <input type="text" name='age' placeholder='Age'  onChange={handleChange}/>
        </div>
        <div>
          <input type="text" name="qualification" placeholder="Qualification" onChange={handleChange} />
        </div>
        <div>
          <input type="number" name="mobile" placeholder="mobile number"  onChange={handleChange} />
        </div>
         <div>
          <input type="file"  />
        </div> 
          <Button >Add</Button>
      </form>
    </div>
  )
}
export default StudentProfile ;







































